function downloadCSV()
{
	document.historicalForm.hiddDwnld.value = true;
	var symbl = escape('RELIANCE');
	var series= $('#otherSeries .sel').html();
	//var series = escape('EQ');
	var dwnld = $("#hiddDwnld").val();
	var frmDat = $("#fromDate").val();
	var toDat = $("#toDate").val(); 
	var perid = $("#period").val();
	window.open("/live_market/dynaContent/live_watch/get_quote/getHistoricalData.jsp?symbol="+symbl+"&series="+series+"&fromDate="+frmDat+"&toDate="+toDat+"&datePeriod="+perid+"&hiddDwnld="+dwnld+"");
	//alert("download csv" + "/live_market/dynaContent/live_watch/get_quote/getHistoricalData.jsp?symbol="+symbl+"&series="+series+"&fromDate="+frmDat+"&toDate="+toDat+"&datePeriod="+perid+"&hiddDwnld="+dwnld+"" );
}

var histScripts=false;
function loadHistoryData(){

	if(!histScripts)
		$.get("/jqueryui/ui/minified/jquery.ui.core.min.js",function(html){
			$(document).append(''+html+'');
			$.get("/jqueryui/ui/minified/jquery.ui.widget.min.js",function(w){
				$(document).append(''+w+'');
					$.get("/jqueryui/ui/minified/jquery.ui.datepicker.min.js",function(d){
						$(document).append(''+d+'');
						loadHistoryDataS();
						histScripts=true;
/*						$(function() {
								$( "#fromDate" ).datepicker({
									dateFormat: 'dd-M-yy',
									changeMonth: true,
									changeYear: true,
									maxDate: new Date()
								});
								$( "#toDate" ).datepicker({
									dateFormat: 'dd-M-yy',
									changeMonth: true,
									changeYear: true//,
									//maxDate: new Date()
								});
							});*/
					});
			});
	});
	else
		loadHistoryDataS();
}
function loadHistoryDataS()
{
				//alert('0');
	var timePeriod = '2weeks';
//	if($('#period').val()!='unselected' && document.forms['historicalForm']['radPeriod'][0].checked){
		if($('#period').val()!='unselected'){
		timePeriod=$('#period').val();
	}
//	else if($('#period').val()=='unselected' && document.forms['historicalForm']['radPeriod'][0].checked){
	else if($('#period').val()=='unselected'){
		timePeriod='2weeks';
	}
	else{
		timePeriod='';
	}

	$.ajax({
		url: '/live_market/dynaContent/live_watch/get_quote/getHistoricalData.jsp'
					+ '?symbol='+escape('RELIANCE')	
					+ '&series=' + $('#otherSeries .sel').html()
					//+ '&series='+escape('EQ')
					+ '&fromDate=' + $('#fromDate').val() 
					+ '&toDate=' + $('#toDate').val() 
					+ '&datePeriod=' +timePeriod ,
		success : function(data, textStatus, XMLHttpRequest) {
			var series = $('#otherSeries .sel').html();
			$("#tryseries").html(series);
			$('#historicalData').html(data);
			$('tr:even').addClass("alt");
			var dur='';
//			if(document.forms['historicalForm']['radPeriod'][0].checked ){				
				if($('#period').val()!='unselected')
				dur= " for the last "+ $('#period').val();//changed By suhas
		/*	}
			else
				dur= " From "+ $('#fromDate').val() +" To "+  $('#toDate').val();//Changed by suhas:23/2/11 for FTR change in Message. */
			$('#historyDuration').html(dur);
			try{
				highlightData();

				}catch(e){}

		}
	});
}

function highlightData(){
	var trIndex=0;
	var tdIndex=0;
	var highP=0.0;
	var lowP=0.0;
	var htrIndex=0;
	var ltrIndex=0;
	var htdIndex=0;
	var ltdIndex=0;
	$("#historicalData table tr").each(function(tri){
			/* changes made by sweta for getquotes*/		
		$(this).find("th").each(function(thi){
			if(thi==1)
			{
				$(this).text("Symbol");
				$(this).attr("title","Symbol");
			}
			if(thi==2)
			{
				$(this).text("Series");
				$(this).attr("title","Series");							
			}
			if(thi==0)
			{
				$(this).text("Date");
				$(this).attr("title","Date");
			}
			if(thi==3)
			{
				$(this).text("Open");
				$(this).attr("title","Open Price");
			}
			if(thi==4)
			{
				$(this).text("High");
				$(this).attr("title","High Price");
			}
			if(thi==5)
			{
				$(this).text("Low");
				$(this).attr("title","Low Price");
			}
			if(thi==6)
			{
				$(this).text("LTP");
				$(this).attr("title","Last Traded Price");
			}
			if(thi==7)
			{
				$(this).text("Close");
				$(this).attr("title","Close Price");
			}
			if(thi==8)
			{
				$(this).text("Volume");
				$(this).attr("title","Total Traded Quantity");
			}
			if(thi==9)
			{
				//$(this).text("Turnover (in Lakhs)");
				//$(this).attr("title","Turnover (in Lakhs)");
				var form1="Turnover";
				var form2="(in Lakhs)";
				var format=form1+"
"+form2;
				$(this).html(format);
				$(this).attr("title","Turnover (in Lakhs)");
			}

		});
		$(this).find("td").each(function(tdi){
				if(tdi==2)
				{	
					$(this).css("text-align","left");
				}

				if(tdi==1)
				{	
					$(this).css("text-align","left");
				}
				if(tdi==0)
				{	
					$(this).attr("class","date");
					//$(this).attr("nowrap","");
					var format = $(this).text();
						//alert("date" + format);
						format = ""+format+"";
					$(this).html(format);
				}

				//if(tdi==2){	
					if(tdi==4){
					var td=$(this).text();
					td = td.replace(',','');
					var tdN = parseFloat(td);									
					if(highP < tdN){
						highP=tdN;
						htrIndex=tri;
						htdIndex=tdi;
					}
				}
				//else if(tdi==3){
					else if(tdi==5){
					var td=$(this).text();
					td = td.replace(',','');
					var tdN = parseFloat(td);					
					if(lowP==0){
						lowP=highP;
						ltrIndex=tri;
						ltdIndex=tdi;
					}
					if(lowP > tdN){
						lowP=tdN;
						ltrIndex=tri;
						ltdIndex=tdi;	
					}				
				}			
			}

		);

		}
	);

	//alert(highP+"==="+lowP);
	//for highlighting
	var htr=$("#historicalData table tr").get(htrIndex);
//	var htd = htr.getElementsByTagName("td")[2];	//2
	var htd = htr.getElementsByTagName("td")[4];
	$(htd).removeClass("high-sel");
	$(htd).removeClass("low-sel");
	$(htd).addClass("low-sel"); //Sonal date:23/03/2011

	var ltr=$("#historicalData table tr").get(ltrIndex);
//	var ltd = ltr.getElementsByTagName("td")[3];			//3
	var ltd = ltr.getElementsByTagName("td")[5];
	$(ltd).removeClass("high-sel");
	$(ltd).removeClass("low-sel");
	$(ltd).addClass("high-sel");	//Sonal date:23/03/2011
}




